import{_ as t,c as d,o as i,b0 as v}from"./chunks/framework.C7kaS1yO.js";const h=JSON.parse('{"title":"行测思维提分手册","description":"掌握行测核心决策逻辑，在有限时间内通过取舍策略获取最高分。","frontmatter":{"title":"行测思维提分手册","description":"掌握行测核心决策逻辑，在有限时间内通过取舍策略获取最高分。"},"headers":[],"relativePath":"extra/thinking/index.md","filePath":"extra/thinking/index.md"}'),o={name:"extra/thinking/index.md"};function r(s,a,l,n,e,c){return i(),d("div",null,[...a[0]||(a[0]=[v('<h1 id="🧠-行测思维提分手册" tabindex="-1" data-v-43315930>🧠 行测思维提分手册 <a class="header-anchor" href="#🧠-行测思维提分手册" aria-label="Permalink to &quot;🧠 行测思维提分手册&quot;" data-v-43315930>​</a></h1><blockquote data-v-43315930><p data-v-43315930><strong data-v-43315930>行测不是考“智商”，而是考“决策”。</strong><br data-v-43315930> 在 120 分钟内处理 130+ 道题目，你的目标不是做完，而是<strong data-v-43315930>得高分</strong>。</p></blockquote><hr data-v-43315930><h2 id="🚀-四大核心底层思维" tabindex="-1" data-v-43315930>🚀 四大核心底层思维 <a class="header-anchor" href="#🚀-四大核心底层思维" aria-label="Permalink to &quot;🚀 四大核心底层思维&quot;" data-v-43315930>​</a></h2><div class="tip custom-block" data-v-43315930><p class="custom-block-title" data-v-43315930>1. 取舍思维：学会放弃是成熟的开始</p><p data-v-43315930>120分钟做135道题，平均每道题 53 秒。</p><ul data-v-43315930><li data-v-43315930><strong data-v-43315930>黄金法则</strong>：超过 2 分钟没思路的题目，必须果断蒙一个然后跳过。</li><li data-v-43315930><strong data-v-43315930>放弃战略</strong>：数量关系（工程问题除外）、极难的几何问题，可以战略性留到最后，没时间就全蒙。</li></ul></div><div class="tip custom-block" data-v-43315930><p class="custom-block-title" data-v-43315930>2. 秒杀思维：避开死磕，寻找出口</p><p data-v-43315930>行测是选择题，选项本身就是提示！</p><ul data-v-43315930><li data-v-43315930><strong data-v-43315930>代入排除</strong>：看到方程组先别列，试试选项。</li><li data-v-43315930><strong data-v-43315930>极端思维</strong>：假设极端情况，排除不可能。</li><li data-v-43315930><strong data-v-43315930>倍数特性</strong>：如果答案是 5 的倍数，立马看尾数。</li></ul></div><div class="tip custom-block" data-v-43315930><p class="custom-block-title" data-v-43315930>3. 客观思维：你要的是“他觉得”</p><ul data-v-43315930><li data-v-43315930><strong data-v-43315930>言语理解</strong>：不要用你的工作经验去反驳文段。哪怕文段说“太阳是凉的”，你也要选“太阳是凉的”。</li><li data-v-43315930><strong data-v-43315930>判断推理</strong>：严格逻辑推导，不准加入任何外部常识。</li></ul></div><div class="tip custom-block" data-v-43315930><p class="custom-block-title" data-v-43315930>4. 敏感度思维：看到题目的瞬间反射</p><ul data-v-43315930><li data-v-43315930><strong data-v-43315930>图形推理</strong>：看到黑白球 -&gt; 找对称/旋转；看到线条 -&gt; 数交点/一笔画。</li><li data-v-43315930><strong data-v-43315930>资料分析</strong>：看到“增长率” -&gt; 找基期、现期；看到“比重” -&gt; 找比例关系。</li></ul></div><hr data-v-43315930><h2 id="📊-五大模块得分优先级策略" tabindex="-1" data-v-43315930>📊 五大模块得分优先级策略 <a class="header-anchor" href="#📊-五大模块得分优先级策略" aria-label="Permalink to &quot;📊 五大模块得分优先级策略&quot;" data-v-43315930>​</a></h2><div class="thinking-grid" data-v-43315930><div class="thinking-card priority-high" data-v-43315930><h3 data-v-43315930>1. 资料分析 (必须全拿)</h3><p data-v-43315930><b data-v-43315930>特点</b>：送分题，只要细心加找准公式，能拿 90%+。</p><p data-v-43315930><b data-v-43315930>策略</b>：放在黄金时间做，绝对不能最后蒙。掌握速算技巧（截位法）。</p></div><div class="thinking-card priority-high" data-v-43315930><h3 data-v-43315930>2. 言语理解 (拉分王)</h3><p data-v-43315930><b data-v-43315930>特点</b>：题量大，分值重，极其主观容易踩坑。</p><p data-v-43315930><b data-v-43315930>策略</b>：抓对应关系，抓转折词。不要读全文，要读逻辑。</p></div><div class="thinking-card priority-medium" data-v-43315930><h3 data-v-43315930>3. 判断推理 (得分稳)</h3><p data-v-43315930><b data-v-43315930>特点</b>：理科生的福利，套路极深。</p><p data-v-43315930><b data-v-43315930>策略</b>：翻译推理必须 100% 正确；类比推理找二级辨析（词性、位置）。</p></div><div class="thinking-card priority-low" data-v-43315930><h3 data-v-43315930>4. 常识判断 (随缘分)</h3><p data-v-43315930><b data-v-43315930>特点</b>：考察范围上至天文下至地理，性价比极低。</p><p data-v-43315930><b data-v-43315930>策略</b>：利用零碎时间（比如排队进场）看。正式考试控制在 5-10 分钟做完。</p></div><div class="thinking-card priority-low" data-v-43315930><h3 data-v-43315930>5. 数量关系 (绝命毒师)</h3><p data-v-43315930><b data-v-43315930>特点</b>：耗时长，难度大，绝大多数人的噩梦。</p><p data-v-43315930><b data-v-43315930>策略</b>：最后 10 分钟做。挑出 3-5 道简单的（工程、行程、坐标），剩下的全蒙。 </p></div></div><hr data-v-43315930><h2 id="💡-给考生的-锦囊秘籍" tabindex="-1" data-v-43315930>💡 给考生的“锦囊秘籍” <a class="header-anchor" href="#💡-给考生的-锦囊秘籍" aria-label="Permalink to &quot;💡 给考生的“锦囊秘籍”&quot;" data-v-43315930>​</a></h2><ol data-v-43315930><li data-v-43315930><strong data-v-43315930>涂卡策略</strong>：做完一个模块涂一个模块，<strong data-v-43315930>千万不要最后统一涂</strong>（防止手抖涂错或没时间）。</li><li data-v-43315930><strong data-v-43315930>草稿纸利用</strong>：资料分析的草稿要整齐，否则你自己算出来的数字待会就找不到了。</li><li data-v-43315930><strong data-v-43315930>心理战</strong>：遇到连着 3 道不会做的题，心态不要崩。告诉自己：我不会，别人大概率也不会。</li></ol><hr data-v-43315930>',15)])])}const g=t(o,[["render",r],["__scopeId","data-v-43315930"]]);export{h as __pageData,g as default};
