import{_ as e,c as l,o as d,b0 as n}from"./chunks/framework.C7kaS1yO.js";const y=JSON.parse('{"title":"考公易错成语大全","description":"","frontmatter":{"title":"考公易错成语大全","aside":false},"headers":[],"relativePath":"idiom/common/index.md","filePath":"idiom/common/index.md"}'),s={name:"idiom/common/index.md"};function a(g,t,r,i,x,o){return d(),l("div",null,[...t[0]||(t[0]=[n('<h1 id="考公常用-易错成语辨析" tabindex="-1">考公常用/易错成语辨析 <a class="header-anchor" href="#考公常用-易错成语辨析" aria-label="Permalink to &quot;考公常用/易错成语辨析&quot;">​</a></h1><p>在公务员考试的“言语理解与表达”模块中，成语辨析是核心考点。许多成语由于字面意思与实际内涵存在偏差，极易产生误用。</p><h2 id="一、-望文生义类-极易望名思义而误用" tabindex="-1">一、 望文生义类 (极易望名思义而误用) <a class="header-anchor" href="#一、-望文生义类-极易望名思义而误用" aria-label="Permalink to &quot;一、 望文生义类 (极易望名思义而误用)&quot;">​</a></h2><table tabindex="0"><thead><tr><th style="text-align:left;">成语</th><th style="text-align:left;">正确含义</th><th style="text-align:left;">常见误区</th></tr></thead><tbody><tr><td style="text-align:left;"><strong>空穴来风</strong></td><td style="text-align:left;">传闻是有根据的</td><td style="text-align:left;">误指毫无根据</td></tr><tr><td style="text-align:left;"><strong>三人成虎</strong></td><td style="text-align:left;">谣言传的人多了就会信以为真</td><td style="text-align:left;">误指人多力量大或团结</td></tr><tr><td style="text-align:left;"><strong>七月流火</strong></td><td style="text-align:left;">天气转凉的征兆</td><td style="text-align:left;">误指天气酷热</td></tr><tr><td style="text-align:left;"><strong>不足为训</strong></td><td style="text-align:left;">不值得作为准则或道理</td><td style="text-align:left;">误指不值得作为教训</td></tr><tr><td style="text-align:left;"><strong>万人空巷</strong></td><td style="text-align:left;">庆祝、欢迎等盛况，人们都出来了</td><td style="text-align:left;">误指街上空无一人</td></tr><tr><td style="text-align:left;"><strong>目无全牛</strong></td><td style="text-align:left;">技艺精湛，达到了得心应手的境界</td><td style="text-align:left;">误指看问题不全面</td></tr><tr><td style="text-align:left;"><strong>差强人意</strong></td><td style="text-align:left;">大体上还能使人满意</td><td style="text-align:left;">误指让人不满意</td></tr><tr><td style="text-align:left;"><strong>文不加点</strong></td><td style="text-align:left;">文章一气呵成，无需修改（点指修改）</td><td style="text-align:left;">误指写文章不加标点</td></tr><tr><td style="text-align:left;"><strong>久假不归</strong></td><td style="text-align:left;">长期借去不归还（假指借）</td><td style="text-align:left;">误指长期请假不回来</td></tr><tr><td style="text-align:left;"><strong>首当其冲</strong></td><td style="text-align:left;">首先受到冲击、遭遇劫难</td><td style="text-align:left;">误指带头、冲在最前面</td></tr></tbody></table><h2 id="二、-适用对象误用类-对象有严格限制" tabindex="-1">二、 适用对象误用类 (对象有严格限制) <a class="header-anchor" href="#二、-适用对象误用类-对象有严格限制" aria-label="Permalink to &quot;二、 适用对象误用类 (对象有严格限制)&quot;">​</a></h2><table tabindex="0"><thead><tr><th style="text-align:left;">成语</th><th style="text-align:left;">适用对象/语境</th><th style="text-align:left;">常见误区</th></tr></thead><tbody><tr><td style="text-align:left;"><strong>美轮美奂</strong></td><td style="text-align:left;">仅形容房屋、建筑</td><td style="text-align:left;">误用于衣服、风景、表演</td></tr><tr><td style="text-align:left;"><strong>鼎力相助</strong></td><td style="text-align:left;">感谢他人的帮助 (敬辞)</td><td style="text-align:left;">误用于自己对别人的帮助</td></tr><tr><td style="text-align:left;"><strong>蓬荜生辉</strong></td><td style="text-align:left;">谦称他人来访使自家增光</td><td style="text-align:left;">误用于赠送礼物或美化环境</td></tr><tr><td style="text-align:left;"><strong>望其项背</strong></td><td style="text-align:left;">有能力赶上 (常用于否定：不可望其项背)</td><td style="text-align:left;">误用于已经超过对方</td></tr><tr><td style="text-align:left;"><strong>人满为患</strong></td><td style="text-align:left;">强调人多造成的困难/麻烦 (贬义)</td><td style="text-align:left;">误用于单纯形容景区、商场热闹</td></tr><tr><td style="text-align:left;"><strong>豆蔻年华</strong></td><td style="text-align:left;">特指少女十三四岁</td><td style="text-align:left;">误用于成年女性或更小的幼儿</td></tr><tr><td style="text-align:left;"><strong>青梅竹马</strong></td><td style="text-align:left;">形容男女儿时亲昵的关系</td><td style="text-align:left;">误用于同性朋友之间</td></tr><tr><td style="text-align:left;"><strong>劳苦功高</strong></td><td style="text-align:left;">多用于形容他人功劳显著</td><td style="text-align:left;">误用于自己夸奖自己</td></tr></tbody></table><h2 id="三、-感情色彩误用类-褒贬颠倒" tabindex="-1">三、 感情色彩误用类 (褒贬颠倒) <a class="header-anchor" href="#三、-感情色彩误用类-褒贬颠倒" aria-label="Permalink to &quot;三、 感情色彩误用类 (褒贬颠倒)&quot;">​</a></h2><table tabindex="0"><thead><tr><th style="text-align:left;">成语</th><th style="text-align:left;">感情色彩</th><th style="text-align:left;">正确内涵</th><th style="text-align:left;">对比词</th></tr></thead><tbody><tr><td style="text-align:left;"><strong>炙手可热</strong></td><td style="text-align:left;">贬义</td><td style="text-align:left;">权势很大，气焰嚣张</td><td style="text-align:left;">对比：【大红大紫】</td></tr><tr><td style="text-align:left;"><strong>始作俑者</strong></td><td style="text-align:left;">贬义</td><td style="text-align:left;">第一个做坏事的人</td><td style="text-align:left;">对比：【创始人】</td></tr><tr><td style="text-align:left;"><strong>无所不为</strong></td><td style="text-align:left;">贬义</td><td style="text-align:left;">什么坏事都干得出</td><td style="text-align:left;">对比：【无所不能】</td></tr><tr><td style="text-align:left;"><strong>处心积虑</strong></td><td style="text-align:left;">贬义</td><td style="text-align:left;">费尽心思盘算(多指坏事)</td><td style="text-align:left;">对比：【殚精竭虑】(褒)</td></tr><tr><td style="text-align:left;"><strong>弹冠相庆</strong></td><td style="text-align:left;">贬义</td><td style="text-align:left;">官场中一人升官，同伙互相庆贺</td><td style="text-align:left;">对比：【欣喜若狂】</td></tr><tr><td style="text-align:left;"><strong>倾巢出动</strong></td><td style="text-align:left;">贬义</td><td style="text-align:left;">全部的敌人都出来活动(含一网打尽之意)</td><td style="text-align:left;">对比：【全员出动】</td></tr><tr><td style="text-align:left;"><strong>不孚众望</strong></td><td style="text-align:left;">贬义</td><td style="text-align:left;">不能使大家信服</td><td style="text-align:left;">对比：【不负众望】(褒)</td></tr><tr><td style="text-align:left;"><strong>胸无城府</strong></td><td style="text-align:left;">褒义</td><td style="text-align:left;">为人坦率，没有心机</td><td style="text-align:left;">误解为“没有心胸”</td></tr></tbody></table><h2 id="四、-高频近义词深度辨析" tabindex="-1">四、 高频近义词深度辨析 <a class="header-anchor" href="#四、-高频近义词深度辨析" aria-label="Permalink to &quot;四、 高频近义词深度辨析&quot;">​</a></h2><table tabindex="0"><thead><tr><th style="text-align:left;">词组对比</th><th style="text-align:left;">共同点</th><th style="text-align:left;">关键区别</th></tr></thead><tbody><tr><td style="text-align:left;"><strong>一目了然</strong> vs <strong>一览无余</strong></td><td style="text-align:left;">看得清</td><td style="text-align:left;">前者侧重“看明白”；后者侧重“无遮挡”</td></tr><tr><td style="text-align:left;"><strong>息息相关</strong> vs <strong>休戚与共</strong></td><td style="text-align:left;">关系紧密</td><td style="text-align:left;">前者泛指密切；后者含利害、祸福的共同承担</td></tr><tr><td style="text-align:left;"><strong>宵衣旰食</strong> vs <strong>披星戴月</strong></td><td style="text-align:left;">辛苦勤奋</td><td style="text-align:left;">前者多指帝王或高级官员勤于政事</td></tr><tr><td style="text-align:left;"><strong>锦上添花</strong> vs <strong>雪中送炭</strong></td><td style="text-align:left;">提供助力</td><td style="text-align:left;">前者“好上加好”；后者“难中相助”</td></tr><tr><td style="text-align:left;"><strong>南辕北辙</strong> vs <strong>背道而驰</strong></td><td style="text-align:left;">方向相反</td><td style="text-align:left;">前者指行动与目的相反；后者指两物发展方向相反</td></tr><tr><td style="text-align:left;"><strong>首屈一指</strong> vs <strong>名列前茅</strong></td><td style="text-align:left;">表现优异</td><td style="text-align:left;">前者指第一名；后者指前几名</td></tr><tr><td style="text-align:left;"><strong>大刀阔斧</strong> vs <strong>雷厉风行</strong></td><td style="text-align:left;">办事果断</td><td style="text-align:left;">前者侧重气魄和手段；后者侧重速度和效率</td></tr></tbody></table>',10)])])}const h=e(s,[["render",a]]);export{y as __pageData,h as default};
